import { useEffect, useState } from "react"
const urlInput = document.querySelector(".form_imput_text");
const result = document.querySelector(".result");

const getLocalStorage = () => {
  let links = localStorage.getItem("links")
  console.log(links)
  if (links) {
    return JSON.parse(localStorage.getItem("links"))
  } else {
    return []
  }

}

const resultCard = function(){
    urlInput.value = "";
    return `<div className="form_table">
              <p className="form_table_paragraph">{links.full_short_link}</p>
              <button onClick={handleCopy} className="form_table_button">
              {buttonText}
              </button>
            </div>`;
}

export default function Form () {
  const [text, setText] = useState("")
  const [links, setLinks] = useState(getLocalStorage())
  const [buttonText, setButtonText] = useState("Copy")

    const handleSubmit = (e) => {
    e.preventDefault()

    if (!text) {
      alert("Input is empty")
    } else {
      const shortenLink = async () => {
        const res = await fetch(`https://api.shrtco.de/v2/shorten?url=${text}`)
        const data = await res.json()
        console.log(data.result)
        setLinks(data.result)
        setText("")
      }

      shortenLink()
    }
    result.insertAdjacentHTML(
      "afterbegin",
      resultCard()
    );
  }

  const handleCopy = () => {
    navigator.clipboard.writeText(links.full_short_link)
    setButtonText("Copied!")
  }

  useEffect(() => {
    localStorage.setItem("links", JSON.stringify(links))
  }, [links])


  return (
    <>
      <div className="form"> 
        <form className="form_container" onSubmit={handleSubmit}>
          <input className="form_imput_text" type="url" placeholder="     Shorten a link here..." value={text} onChange={(e) => setText(e.target.value)}/>
          <div id="error"></div>
          <input className="form_imput_submit" type="submit" value="Shorten It!" onClick={handleSubmit}/> 
        </form>
        <div class="result"></div>
      </div>
    </>

  )
}
