  gap: 0;
  width: 350px;
  height: 335px;
  background: hsl(257, 27%, 26%);
  top: 86px;
  text-align: center;
  border-radius: 10px;

    @media(min-width:997px){
      position: initial;
      gap: 40px;
      display: flex;
      align-items: center;
      background: none;
      flex-direction: row;
      margin: none;
      align-content: center;
    }
}

  .nav_items.active{
    opacity: 1;
  }
  .nav_logo{
  height: 30px;
  margin-top: 6px;
}

  .nav_item{

  font-weight: bold;
  font-size: 22px;
  z-index: 1;
  color: #fff;
